import React from "react";
import { Column, Row } from "styled-grid-system-component";
import { Container } from "styled-container-component";
import Select from "react-select";
import {
	SectionBookVisit,
	InputTag,
	Text,
	FlexTag,
	SpaceTag,
	BookAVistitButton
} from "../../components/small-component";

export type BookVisitProps = {
	id?: string;
};
export function BookVisit({ id }: BookVisitProps) {
	const [selectedOption, setSelectedOption] = React.useState(null);
	const options = [
		{ value: "chocolate", label: "Chocolate" },
		{ value: "strawberry", label: "Strawberry" },
		{ value: "vanilla", label: "Vanilla" }
	];
	function handleChange(selectedOption) {
		setSelectedOption(selectedOption);
		console.log(`Option selected:`, selectedOption);
	}
	return (
		<SectionBookVisit id={id}>
			<Container>
				<Row>
					<Column sm={4} xs={12} md={4}>
						<SpaceTag marginTop="20">
							<Text fontSize="26px" color="#000" fontWeight="900">
								Book a Visit
							</Text>
						</SpaceTag>
					</Column>
				</Row>
				<Row>
					<Column sm={4} xs={12} md={4}>
						<SpaceTag marginTop="20">
							<FlexTag>
								<SpaceTag marginTop="15">
									<Text
										fontSize="18px"
										color="#000"
										fontWeight="500"
									>
										Phone No
									</Text>
								</SpaceTag>
								<InputTag
									value=""
									placeholder="enter your phone no"
								/>
							</FlexTag>
						</SpaceTag>
					</Column>
					<Column sm={4} xs={12} md={4}>
						<SpaceTag marginTop="18">
							<FlexTag>
								<SpaceTag marginTop="15">
									<Text
										fontSize="20px"
										color="#000"
										fontWeight="500"
									>
										Location
									</Text>
								</SpaceTag>
								<Select
									value={selectedOption}
									onChange={handleChange}
									options={options}
								/>
							</FlexTag>
						</SpaceTag>
					</Column>
					<Column sm={4} xs={12} md={4}>
						<SpaceTag marginTop="30">
							<BookAVistitButton>Book a Visit</BookAVistitButton>
						</SpaceTag>
					</Column>
				</Row>
			</Container>
		</SectionBookVisit>
	);
}
