export function date(timestamp) {
  var zone = new Date(timestamp);
  var date = zone.getDate();
  return date;
}
export function month(timestamp) {
  var zone = new Date(timestamp);
  var months = ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"];
  var month = months[zone.getMonth()];
  return month;
}
export function year(timestamp) {
  var zone = new Date(timestamp);
  var year = zone.getFullYear();
  return year;
}
export function hour(timestamp) {
  var zone = new Date(timestamp);
  var hour = zone.getHours();
  return hour;
}
export function minutes(timestamp) {
  var zone = new Date(timestamp);
  var minutes = zone.getMinutes().toString().length === 1 ? "0" + zone.getMinutes() : zone.getMinutes();
  return minutes;
}
export function seconds(timestamp) {
  var time = new Date(timestamp);
  var seconds = time.getSeconds().toString().length === 1 ? "0" + time.getSeconds() : time.getSeconds();
  return seconds;
}
