import React from "react";
import Link from "next/link";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import Modal from "react-modal";
import SelectSearch from "react-select-search/dist/cjs/index.js";
import { Column, Row } from "styled-grid-system-component";
import {
	FlexTag,
	Text,
	BannerImage,
	SpaceTag,
	SectionBannerRight,
	BannerCircle,
	CenterTag,
	BookVisitButton,
	BannerButton,
	FlyButton,
	MenuButton,
	SearchIconCircle,
	SearchBlock,
	SearchInputTag,
	FloatingTag
} from "../../components/small-component";
import { Icon } from "../../components/icons/icon";
import { Icons } from "../../components/icons/icons-props";
import * as BannerImg from "../../img/banner.png";
export type BannerProps = {
	onClickEvent?: (event: React.MouseEvent) => void;
};
export function BannerMobile({ onClickEvent }: BannerProps) {
	const images = [
		{
			img: BannerImg
		},

		{
			img: BannerImg
		},
		{
			img: BannerImg
		},
		{
			img: BannerImg
		}
	];
	const [currentSlideIdx, setCurrentSlideIdx] = React.useState(0);

	const prevSlide = () => {
		const resetToVeryBack = currentSlideIdx === 0;
		const index = resetToVeryBack ? images.length - 1 : currentSlideIdx - 1;
		setCurrentSlideIdx(index);
	};
	const nextSlide = () => {
		const resetIndex = currentSlideIdx === images.length - 1;
		const index = resetIndex ? 0 : currentSlideIdx + 1;
		setCurrentSlideIdx(index);
	};
	const activeImageSourcesFromState = images.slice(
		currentSlideIdx,
		currentSlideIdx + 3
	);
	const imageSourcesToDisplay =
		activeImageSourcesFromState.length < 3
			? [
					...activeImageSourcesFromState,
					...images.slice(0, 3 - activeImageSourcesFromState.length)
			  ]
			: activeImageSourcesFromState;
	const settings = {
		dots: false,
		infinite: true,
		slidesToShow: 1,
		slidesToScroll: 1,
		autoplay: true,
		autoplaySpeed: 2000
	};
	const customStyles = {
		content: {
			top: "50%",
			left: "50%",
			right: "auto",
			bottom: "auto",
			marginRight: "-50%",
			transform: "translate(-50%, -50%)",
			background: "#000000e3",
			width: "100%",
			height: "100%"
		}
	};
	const [modalIsOpen, setIsOpen] = React.useState(false);
	function openModal() {
		setIsOpen(true);
	}

	function closeModal() {
		setIsOpen(false);
	}
	const options = [
		{ name: "Swedish", value: "sv" },
		{ name: "English", value: "en" }
	];
	function fuzzySearch(options) {
		const fuse = new Fuse(options, {
			keys: ["name", "groupName"],
			threshold: 0.3
		});

		return value => {
			if (!value.length) {
				return options;
			}

			return fuse.search(value);
		};
	}
	return (
		<>
			<Row>
				<Column
					sm={8}
					md={8}
					xs={12}
					style={{ paddingRight: "0px", paddingLeft: "0px" }}
				>
					<div style={{ position: "relative" }}>
						<Slider {...settings}>
							{imageSourcesToDisplay.map((items, i) => (
								<div key={i}>
									<BannerImage banner={items.img}>
										<FloatingTag>
											<SpaceTag
												marginLeft="20"
												marginRight="20"
											>
												<FlexTag justifyContent="flex-end">
													<MenuButton
														onClick={openModal}
													>
														<Icon
															name={Icons.menu}
														/>
													</MenuButton>
												</FlexTag>
											</SpaceTag>
										</FloatingTag>
									</BannerImage>
								</div>
							))}
						</Slider>
					</div>
					<FlyButton>
						<FlexTag justifyContent="space-between">
							<BannerButton onClick={prevSlide}>
								<Icon name={Icons.left} />
								previous
							</BannerButton>

							<BannerButton onClick={nextSlide}>
								next
								<Icon name={Icons.right} />
							</BannerButton>
						</FlexTag>
					</FlyButton>
				</Column>
				<Column
					sm={4}
					md={4}
					xs={12}
					style={{ paddingRight: "0px", paddingLeft: "0px" }}
				>
					<SectionBannerRight>
						<FloatingTag>
							<SpaceTag marginLeft="20" marginRight="20">
								<FlexTag justifyContent="center">
									<SearchBlock>
										<FlexTag>
											<SpaceTag
												marginLeft="10"
												marginRight="10"
												marginTop="6"
											>
												<Icon name={Icons.map} />
											</SpaceTag>

											<SpaceTag
												marginLeft="10"
												marginRight="10"
												marginTop="6"
											>
												<Icon name={Icons.downArrow} />
											</SpaceTag>
											<SpaceTag
												marginLeft="10"
												marginRight="10"
												marginTop="6"
											>
												<Text
													fontSize="14px"
													color="#000"
													fontWeight="900"
												>
													Where you want to stay in
												</Text>
