export function date(timestamp) {
  const zone = new Date(timestamp);
  const date = zone.getDate();
  return date;
}
export function month(timestamp) {
  const zone = new Date(timestamp);
  var months = ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"];
  const month = months[zone.getMonth()];
  return month;
}
export function year(timestamp) {
  const zone = new Date(timestamp);
  const year = zone.getFullYear();
  return year;
}
export function hour(timestamp) {
  const zone = new Date(timestamp);
  const hour = zone.getHours().toString().length === 1 ? "0" + zone.getHours() : zone.getHours();
  return hour;
}
export function minutes(timestamp) {
  const zone = new Date(timestamp);
  const minutes = zone.getMinutes().toString().length === 1 ? "0" + zone.getMinutes() : zone.getMinutes();
  return minutes;
}
export function seconds(timestamp) {
  const time = new Date(timestamp);
  const seconds = time.getSeconds().toString().length === 1 ? "0" + time.getSeconds() : time.getSeconds();
  return seconds;
}
